import{isTag,isCDATA,isText,hasChildren,isComment}from"domhandler";import renderHTML from"dom-serializer";import{ElementType}from"domelementtype";export function getOuterHTML(e,t){return renderHTML(e,t)}export function getInnerHTML(e,t){return hasChildren(e)?e.children.map((e=>getOuterHTML(e,t))).join(""):""}export function getText(e){return Array.isArray(e)?e.map(getText).join(""):isTag(e)?"br"===e.name?"\n":getText(e.children):isCDATA(e)?getText(e.children):isText(e)?e.data:""}export function textContent(e){return Array.isArray(e)?e.map(textContent).join(""):hasChildren(e)&&!isComment(e)?textContent(e.children):isText(e)?e.data:""}export function innerText(e){return Array.isArray(e)?e.map(innerText).join(""):hasChildren(e)&&(e.type===ElementType.Tag||isCDATA(e))?innerText(e.children):isText(e)?e.data:""}
